// Intercept login API
cy.intercept('POST', '/api/v1/login').as('tutorLogin');
// Perform UI Login
_loginPage.loginPage.login(this.creds.email, this.creds.password);
cy.wait('@tutorLogin').its('response.statusCode').should('eq', 200);
// Ensure T&C Modal is visible
cy.get('.modal-content', {
  timeout: 10000
}).should('be.visible');
// Scroll & Click "I Agree" Button
cy.contains('button', 'I Agree').scrollIntoView().should('be.visible').click();
// Verify redirected to Tutor Dashboard
cy.url().should('include', '/tutor');
// Verify Welcome message
cy.contains('Welcome, DEMO.TUTOR', {
  timeout: 10000
}).should('be.visible');
// Verify Token in LocalStorage
cy.verifyToken();

